/**
 * Declaration of module types for various file types such as images, CSS, audio files,
 * and document files. This allows TypeScript to recognize and import these file types without type errors.
 */
declare module '*.png';
declare module '*.jpg';
declare module '*.jpeg';
declare module '*.gif';
declare module '*.svg';
declare module '*.css';
declare module '*.wav';
declare module '*.mp3';
declare module '*.m4a';
declare module '*.rdf';
declare module '*.ttl';
declare module '*.pdf';
